import{_ as e,o as t,c as a,a as r}from"./app.6dd1bb2b.js";const u=JSON.parse('{"title":"Wallets","description":"","frontmatter":{},"headers":[{"level":2,"title":"Mixin Messenger","slug":"mixin-messenger","link":"#mixin-messenger","children":[]},{"level":2,"title":"Fennec","slug":"fennec","link":"#fennec","children":[]},{"level":2,"title":"Metamask & WalletConnect compatible wallets","slug":"metamask-walletconnect-compatible-wallets","link":"#metamask-walletconnect-compatible-wallets","children":[]}],"relativePath":"guide/wallets.md"}'),n={name:"guide/wallets.md"},o=r('<h1 id="wallets" tabindex="-1">Wallets <a class="header-anchor" href="#wallets" aria-hidden="true">#</a></h1><h2 id="mixin-messenger" tabindex="-1">Mixin Messenger <a class="header-anchor" href="#mixin-messenger" aria-hidden="true">#</a></h2><p><a href="https://mixin.network/messenger" target="_blank" rel="noreferrer">Mixin Messenger</a> is an open source cryptocurrency wallet, which supports almost all popular cryptocurrencies.</p><p>Mixin Messenger is developed by Mixin Dev Team, who release it under the GPL-3.0 license.</p><p>For users, please download the app from its <a href="https://mixin.network/messenger" target="_blank" rel="noreferrer">official website</a>.</p><p>For developers, please check out <a href="https://github.com/MixinNetwork/" target="_blank" rel="noreferrer">its source code here</a>.</p><h2 id="fennec" tabindex="-1">Fennec <a class="header-anchor" href="#fennec" aria-hidden="true">#</a></h2><p><a href="https://pando.im/fennec" target="_blank" rel="noreferrer">Fennec</a> is an open source browser extension that allows you to easily manage your own crypto assets wallet of Mixin Network.</p><p>You can install Fennec from Chrome web store or Firefox Add-ons:</p><ul><li><a href="https://chrome.google.com/webstore/detail/fennec/eincngenkhohbbfpkohipekcmnkfamjp" target="_blank" rel="noreferrer">Chrome Store</a></li><li><a href="https://addons.mozilla.org/en-US/firefox/addon/fennec/" target="_blank" rel="noreferrer">Firefox Add-ons</a></li></ul><p>Fennec uses Mixin Network&#39;s keystore files to identify your assets and manage your wallet. There are two ways to create your keystore files.</p><p>The first option is to create a dApp from Mixin Developer Dashboard. After creating the dApp, you can generate a keystore file. The second option is to use the <a href="https://github.com/fox-one/fennec-asp" target="_blank" rel="noreferrer">Account Service Providers</a> to generate a keystore file.</p><p>Account Service Provider is also an open source program that creating valid keystore files for you. You can host an instance yourself or use one from the 3rd party hoster.</p><div class="warning custom-block"><p class="custom-block-title">CAUTION</p><p>The hoster has the permission to view the transactions and assets of your keystore (but no permission to operate your keystore). Please make sure you trust the 3rd party hoster before using it.</p></div><h2 id="metamask-walletconnect-compatible-wallets" tabindex="-1">Metamask &amp; WalletConnect compatible wallets <a class="header-anchor" href="#metamask-walletconnect-compatible-wallets" aria-hidden="true">#</a></h2><p>Pando supports Metamask and WalletConnect compatible wallets by <a href="https://mvm.app/" target="_blank" rel="noreferrer">MVM</a>, which is an EVM compatible virtual machine running on Mixin Network.</p><p>MVM provides a standard interface for Metamask and WalletConnect compatible wallets to interact with Pando. You may want to check out <a href="https://mvm.dev/" target="_blank" rel="noreferrer">MVM&#39;s developer documentation</a> to learn more about it.</p>',17),s=[o];function l(i,c,p,h,d,m){return t(),a("div",null,s)}const k=e(n,[["render",l]]);export{u as __pageData,k as default};
