import{_ as t,o as e,c as a,a as r}from"./app.6776b379.js";const d="/assets/mtg_design.267036ee.webp",o="/assets/pando-syncer.2c2b6906.webp",i="/assets/pando-payee.3ae4062b.webp",n="/assets/pando-cashier.a98faadf.webp",f=JSON.parse('{"title":"Pando Leaf Design Document","description":"","frontmatter":{},"headers":[{"level":2,"title":"Interact with Pando","slug":"interact-with-pando","link":"#interact-with-pando","children":[{"level":3,"title":"Mixin Multisig Output","slug":"mixin-multisig-output","link":"#mixin-multisig-output","children":[]},{"level":3,"title":"TransactionAction Definition","slug":"transactionaction-definition","link":"#transactionaction-definition","children":[]}]},{"level":2,"title":"Workers","slug":"workers","link":"#workers","children":[{"level":3,"title":"Syncer Workflow","slug":"syncer-workflow","link":"#syncer-workflow","children":[]},{"level":3,"title":"Payee Workflow","slug":"payee-workflow","link":"#payee-workflow","children":[]},{"level":3,"title":"Assigner & Cashier & TxSender Workflow","slug":"assigner-cashier-txsender-workflow","link":"#assigner-cashier-txsender-workflow","children":[]}]},{"level":2,"title":"Actions","slug":"actions","link":"#actions","children":[{"level":3,"title":"Sys - system operations","slug":"sys-system-operations","link":"#sys-system-operations","children":[]},{"level":3,"title":"Proposal - governance system","slug":"proposal-governance-system","link":"#proposal-governance-system","children":[]},{"level":3,"title":"Cat - manager collaterals","slug":"cat-manager-collaterals","link":"#cat-manager-collaterals","children":[]},{"level":3,"title":"Vat - manager vaults","slug":"vat-manager-vaults","link":"#vat-manager-vaults","children":[]},{"level":3,"title":"Flip - manager auctions","slug":"flip-manager-auctions","link":"#flip-manager-auctions","children":[]},{"level":3,"title":"Oracle - manager price oracle","slug":"oracle-manager-price-oracle","link":"#oracle-manager-price-oracle","children":[]}]}],"relativePath":"references/leaf/design.md"}'),s={name:"references/leaf/design.md"},h=r('<h1 id="pando-leaf-design-document" tabindex="-1">Pando Leaf Design Document <a class="header-anchor" href="#pando-leaf-design-document" aria-hidden="true">#</a></h1><h2 id="interact-with-pando" tabindex="-1">Interact with Pando <a class="header-anchor" href="#interact-with-pando" aria-hidden="true">#</a></h2><p>All participants of Pando complete the interaction by transferring tokens to the multisig wallet. Node worker <strong>Syncer</strong> syncs the payments as mixin multisig outputs; another worker <strong>Payee</strong> processes all outputs in order.</p><p><img src="'+d+'" alt="Pando Design"></p><h3 id="mixin-multisig-output" tabindex="-1">Mixin Multisig Output <a class="header-anchor" href="#mixin-multisig-output" aria-hidden="true">#</a></h3><p><strong>Output:</strong></p><table><thead><tr><th>field</th><th>description</th></tr></thead><tbody><tr><td>Sender</td><td>user mixin id</td></tr><tr><td>CreatedAt</td><td>payment time</td></tr><tr><td>AssetID</td><td>payment asset id</td></tr><tr><td>Amount</td><td>payment amount</td></tr><tr><td>Memo</td><td>extra message</td></tr></tbody></table><p><strong>Output Memo:</strong></p><p>Memo contain the <strong>TransactionAction</strong> information, see details in <a href="https://github.com/fox-one/pando/blob/main/core/action.go" target="_blank" rel="noreferrer">DecodeTransactionAction</a>.</p><p>The memo is maybe AES-encrypted, an ed25519 public key used for compound AES key/iv will be in the first 32 bytes.</p><h3 id="transactionaction-definition" tabindex="-1">TransactionAction Definition <a class="header-anchor" href="#transactionaction-definition" aria-hidden="true">#</a></h3><table><thead><tr><th>field</th><th>description</th><th>type</th></tr></thead><tbody><tr><td>FollowID</td><td>user defined trace id for this transaction</td><td>uuid</td></tr><tr><td>Body</td><td>action type &amp; relevant parameters</td><td>bytes</td></tr></tbody></table><h2 id="workers" tabindex="-1">Workers <a class="header-anchor" href="#workers" aria-hidden="true">#</a></h2><ol><li><strong>Syncer</strong> sync unhanded utxo by mixin messenger api &amp; store into WalletStore as <strong>outputs</strong> in updated asc order.</li><li><strong>Payee</strong> pull unhanded utxo from WalletStore in order and parse memo to get the action then handle it. Transfers may be created during handling.</li><li><strong>Assigner</strong> select enough unspent UTXO and assign to a pending transfer.</li><li><strong>Cashier</strong> pull unhandled transfers from WalletStore in order, then request &amp; sign multisig transfer. If enough signatures collected, generate a raw transaction.</li><li><strong>TxSender</strong> commit raw transactions to Mixin Network.</li></ol><h3 id="syncer-workflow" tabindex="-1">Syncer Workflow <a class="header-anchor" href="#syncer-workflow" aria-hidden="true">#</a></h3><p><img src="'+o+'" alt="Syncer Workflow"></p><h3 id="payee-workflow" tabindex="-1">Payee Workflow <a class="header-anchor" href="#payee-workflow" aria-hidden="true">#</a></h3><p><img src="'+i+'" alt="Payee Workflow"></p><h3 id="assigner-cashier-txsender-workflow" tabindex="-1">Assigner &amp; Cashier &amp; TxSender Workflow <a class="header-anchor" href="#assigner-cashier-txsender-workflow" aria-hidden="true">#</a></h3><p><img src="'+n+'" alt="Assigner &amp; Cashier Workflow"></p><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-hidden="true">#</a></h2><p>All actions supported by Pando with groups cat,flip,oracle,proposal,sys and vat. see <a href="https://github.com/fox-one/pando/blob/main/core/action.go" target="_blank" rel="noreferrer">core/action</a> for details.</p><h3 id="sys-system-operations" tabindex="-1">Sys - system operations <a class="header-anchor" href="#sys-system-operations" aria-hidden="true">#</a></h3><h4 id="_1-withdraw" tabindex="-1">#1 Withdraw <a class="header-anchor" href="#_1-withdraw" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/sys/withdraw.go</p></blockquote><p>withdraw any assets from the multisig wallet, proposal required.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>asset</td><td>uuid</td><td>withdraw asset id</td></tr><tr><td>amount</td><td>uuid</td><td>withdraw amount</td></tr><tr><td>opponent</td><td>uuid</td><td>receiver&#39;s mixin id</td></tr></tbody></table><h3 id="proposal-governance-system" tabindex="-1">Proposal - governance system <a class="header-anchor" href="#proposal-governance-system" aria-hidden="true">#</a></h3><h4 id="_11-make" tabindex="-1">#11 Make <a class="header-anchor" href="#_11-make" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/proposal/make.go</p></blockquote><p>create a new proposal</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>data</td><td>bytes</td><td>action type &amp; parameters will be executed if passed</td></tr></tbody></table><h4 id="_12-shout" tabindex="-1">#12 Shout <a class="header-anchor" href="#_12-shout" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/proposal/shout.go</p></blockquote><p>request node administrator to vote for this proposal</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>proposal trace id</td></tr></tbody></table><h4 id="_13-vote" tabindex="-1">#13 Vote <a class="header-anchor" href="#_13-vote" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/proposal/vote.go</p></blockquote><p>vote for a proposal, nodes only. If enough votes collected, the attached action will be executed on all nodes automatically.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>proposal trace id</td></tr></tbody></table><h3 id="cat-manager-collaterals" tabindex="-1">Cat - manager collaterals <a class="header-anchor" href="#cat-manager-collaterals" aria-hidden="true">#</a></h3><h4 id="_21-create" tabindex="-1">#21 Create <a class="header-anchor" href="#_21-create" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/cat/create.go</p></blockquote><p>create a new collateral type, proposal required.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>gem</td><td>uuid</td><td>collateral asset id</td></tr><tr><td>dai</td><td>uuid</td><td>debt asset id</td></tr><tr><td>name</td><td>string</td><td>collateral type name</td></tr></tbody></table><h4 id="_22-supply" tabindex="-1">#22 Supply <a class="header-anchor" href="#_22-supply" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/cat/supply.go</p></blockquote><p>supply dai token to increase the total debt ceiling for this collateral type. Payment asset id must be equal to the debt asset id.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>collateral trace id</td></tr></tbody></table><h4 id="_23-edit" tabindex="-1">#23 Edit <a class="header-anchor" href="#_23-edit" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/cat/edit.go</p></blockquote><p>modify collateral&#39;s one or more attributes, proposal required.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>collateral trace id</td></tr><tr><td>key</td><td>string</td><td>attribute name</td></tr><tr><td>value</td><td>string</td><td>attributes value</td></tr></tbody></table><h4 id="_24-fold" tabindex="-1">#24 Fold <a class="header-anchor" href="#_24-fold" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/cat/fold.go</p></blockquote><p>modify the debt multiplier(rate), creating / destroying corresponding debt.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>collateral trace id</td></tr></tbody></table><h3 id="vat-manager-vaults" tabindex="-1">Vat - manager vaults <a class="header-anchor" href="#vat-manager-vaults" aria-hidden="true">#</a></h3><h4 id="_31-open" tabindex="-1">#31 Open <a class="header-anchor" href="#_31-open" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/vat/open.go</p></blockquote><p>open a new vault with the special collateral type</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>collateral trace id</td></tr><tr><td>debt</td><td>decimal</td><td>initial debt</td></tr></tbody></table><h4 id="_32-deposit" tabindex="-1">#32 Deposit <a class="header-anchor" href="#_32-deposit" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/vat/deposit.go</p></blockquote><p>transfer collateral into a Vault.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>vault trace id</td></tr></tbody></table><h4 id="_33-withdraw" tabindex="-1">#33 Withdraw <a class="header-anchor" href="#_33-withdraw" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/vat/withdraw.go</p></blockquote><p>withdraw collateral from a Vault, vault owner only.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>vault trace id</td></tr><tr><td>dink</td><td>decimal</td><td>change in collateral</td></tr></tbody></table><h4 id="_34-payback" tabindex="-1">#34 Payback <a class="header-anchor" href="#_34-payback" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/vat/payback.go</p></blockquote><p>decrease Vault debt.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>vault trace id</td></tr></tbody></table><h4 id="_35-generate" tabindex="-1">#35 Generate <a class="header-anchor" href="#_35-generate" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/vat/generate.go</p></blockquote><p>increase Vault debt, vault owner only.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>vault trace id</td></tr><tr><td>debt</td><td>decimal</td><td>change in debt</td></tr></tbody></table><h3 id="flip-manager-auctions" tabindex="-1">Flip - manager auctions <a class="header-anchor" href="#flip-manager-auctions" aria-hidden="true">#</a></h3><h4 id="_41-kick" tabindex="-1">#41 Kick <a class="header-anchor" href="#_41-kick" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/flip/kick.go</p></blockquote><p>put collateral up for auction from an unsafe vault.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>vault trace id</td></tr></tbody></table><h4 id="_42-bid" tabindex="-1">#42 Bid <a class="header-anchor" href="#_42-bid" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/flip/bid.go</p></blockquote><p>pay dai to participate in the auction.</p><blockquote><p>Starting in the tend-phase, bidders compete for a fixed lot amount of Gem with increasing bid amounts of Dai. Once tab amount of Dai has been raised, the auction moves to the dent-phase. The point of the tend phase is to raise Dai to cover the system&#39;s debt. During the dent-phase bidders compete for decreasing lot amounts of Gem for the fixed tab amount of Dai. Forfeited Gem is returned to the liquidated vault for the owner to retrieve. The point of the dent phase is to return as much collateral to the Vault holder as the market will allow.</p></blockquote><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>flip trace id</td></tr><tr><td>lot</td><td>decimal</td><td>collateral amount</td></tr></tbody></table><h4 id="_43-deal" tabindex="-1">#43 Deal <a class="header-anchor" href="#_43-deal" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/flip/deal.go</p></blockquote><p>claim a winning bid / settles a completed auction</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>flip trace id</td></tr></tbody></table><h3 id="oracle-manager-price-oracle" tabindex="-1">Oracle - manager price oracle <a class="header-anchor" href="#oracle-manager-price-oracle" aria-hidden="true">#</a></h3><h4 id="_51-create" tabindex="-1">#51 Create <a class="header-anchor" href="#_51-create" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/oracle/create.go</p></blockquote><p>register a new oracle for the asset, proposal required.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>asset id</td></tr><tr><td>price</td><td>decimal</td><td>initial price</td></tr><tr><td>hop</td><td>int64</td><td>time delay in seconds between poke calls</td></tr><tr><td>threshold</td><td>int64</td><td>number of governors required when poke</td></tr><tr><td>ts</td><td>timestamp</td><td>request timestamp</td></tr></tbody></table><h4 id="_52-edit" tabindex="-1">#52 Edit <a class="header-anchor" href="#_52-edit" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/oracle/edit.go</p></blockquote><p>modify an oracle&#39;s next price, hop &amp; threshold, proposal required.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>asset id</td></tr><tr><td>key</td><td>string</td><td>attribute name</td></tr><tr><td>value</td><td>string</td><td>attributes value</td></tr></tbody></table><h4 id="_53-poke" tabindex="-1">#53 Poke <a class="header-anchor" href="#_53-poke" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/oracle/poke.go</p></blockquote><p>updates the current feed value and queue up the next one.</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>asset id</td></tr><tr><td>price</td><td>decimal</td><td>new next price</td></tr><tr><td>ts</td><td>timestamp</td><td>request timestamp</td></tr></tbody></table><h4 id="_54-rely" tabindex="-1">#54 Rely <a class="header-anchor" href="#_54-rely" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/oracle/rely.go</p></blockquote><p>add a new price feed to the whitelist, proposal required</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>feed mixin id</td></tr><tr><td>key</td><td>bytes</td><td>public key</td></tr></tbody></table><h4 id="_55-deny" tabindex="-1">#55 Deny <a class="header-anchor" href="#_55-deny" aria-hidden="true">#</a></h4><blockquote><p>pkg/maker/oracle/deny.go</p></blockquote><p>remove a price feed from the whitelist, proposal required</p><p><strong>Parameters:</strong></p><table><thead><tr><th>name</th><th>type</th><th>description</th></tr></thead><tbody><tr><td>id</td><td>uuid</td><td>feed mixin id</td></tr></tbody></table>',134),l=[h];function p(c,u,b,m,g,y){return e(),a("div",null,l)}const w=t(s,[["render",p]]);export{f as __pageData,w as default};
